            for (var i=0;i<length;i++)
            {
                if (!Pass(in span0[i], in span1[i], in span2[i], in span3[i], in span4[i])) continue;
                action(table[i], ref span0[i], ref span1[i], ref span2[i], ref span3[i], ref span4[i]);
            }
        }

